targets.expr:
(
  # Failed notifications from ruler to Alertmanager (handling the case the ruler metrics are missing).
  ((sum(rate(cortex_prometheus_notifications_errors_total{cluster=~"$cluster", job=~"($namespace)/((ruler|cortex|mimir|mimir-backend.*))"}[$__rate_interval]))
) or vector(0))
  +
  # Failed notifications from Alertmanager to receivers (handling the case the alertmanager metrics are missing).
  ((sum(cluster_job_integration:cortex_alertmanager_notifications_failed_total:rate5m{cluster=~"$cluster", job=~"($namespace)/((alertmanager|cortex|mimir|mimir-backend.*))"})
) or vector(0))
)
/
(
  # Total notifications from ruler to Alertmanager (handling the case the ruler metrics are missing).
  ((sum(rate(cortex_prometheus_notifications_sent_total{cluster=~"$cluster", job=~"($namespace)/((ruler|cortex|mimir|mimir-backend.*))"}[$__rate_interval]))
) or vector(0))
  +
  # Total notifications from Alertmanager to receivers (handling the case the alertmanager metrics are missing).
  ((sum(cluster_job_integration:cortex_alertmanager_notifications_total:rate5m{cluster=~"$cluster", job=~"($namespace)/((alertmanager|cortex|mimir|mimir-backend.*))"})
) or vector(0))
)
