targets.expr:
(
  sum by (workload) (
    label_replace(label_replace(label_replace(
      kube_deployment_status_replicas_updated{cluster=~"$cluster", namespace=~"$namespace", deployment=~"mimir.*"}
      or
      kube_statefulset_status_replicas_updated{cluster=~"$cluster", namespace=~"$namespace", deployment=~"mimir.*"}
    , "workload", "$1", "deployment", "(.+)"), "workload", "$1", "statefulset", "(.+)"), "workload", "$1", "workload", "(.*?)(?:-zone-[a-z])?")
  )
  /
  sum by (workload) (
    label_replace(label_replace(label_replace(
      kube_deployment_status_replicas{cluster=~"$cluster", namespace=~"$namespace", deployment=~"mimir.*"}
      or
      kube_statefulset_status_replicas{cluster=~"$cluster", namespace=~"$namespace", deployment=~"mimir.*"}
    , "workload", "$1", "deployment", "(.+)"), "workload", "$1", "statefulset", "(.+)"), "workload", "$1", "workload", "(.*?)(?:-zone-[a-z])?")
  )
) and (
  sum by (workload) (
    label_replace(label_replace(label_replace(
      kube_deployment_status_replicas{cluster=~"$cluster", namespace=~"$namespace", deployment=~"mimir.*"}
      or
      kube_statefulset_status_replicas{cluster=~"$cluster", namespace=~"$namespace", deployment=~"mimir.*"}
    , "workload", "$1", "deployment", "(.+)"), "workload", "$1", "statefulset", "(.+)"), "workload", "$1", "workload", "(.*?)(?:-zone-[a-z])?")
  )
  > 0
)

